mapper.select:
select rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from (select rownum rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from (select iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from hotel_inquiry
where title like '%'||#{keyword}||'%' order by idate desc)
where hotelname like '%'||#{keyword}||'%' order by idate desc)
where category like '%'||#{keyword}||'%' order by idate desc)
) where mid = #{mid}


	SELECT RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, RCOUNT FROM
		(SELECT ROWNUM RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, rcount FROM
			(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, count(rcount)  rcount FROM
				(SELECT hi.IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, ri.iid rcount
				FROM hotel_inquiry hi, reply_inquiry ri 
				where hi.iid = ri.iid(+)
				)
				GROUP BY IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID
				order by idate desc
			)   
			WHERE MID=#{mid} 
		) 
		WHERE rno between #{start} and #{end}

select rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from (select rownum rno, iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from (select iid, hotelname, category, title, content, secret, secretnum, ifile, isfile, idate, mid from hotel_inquiry
where title like '%'||#{keyword}||'%' order by idate desc)
where mid like '%'||#{keyword}||'%' order by idate desc)
where hotelname like '%'||#{keyword}||'%' order by idate desc)
where category like '%'||#{keyword}||'%' order by idate desc)
) where rno between #{start} and #{end}


	SELECT RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, RCOUNT FROM
		(SELECT ROWNUM RNO, IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, rcount FROM
			(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, count(rcount)  rcount FROM
				(SELECT hi.IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID, ri.iid rcount
				FROM hotel_inquiry hi, reply_inquiry ri 
				where hi.iid = ri.iid(+)
				)
				GROUP BY IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, IDATE, MID
				order by idate desc
			)      
		) 
	WHERE RNO BETWEEN #{start} AND #{end}


	SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, ifile, isfile, IDATE, MID, RCOUNT FROM
		(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, ifile, isfile, IDATE, MID, rcount FROM
			(SELECT IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, ifile, isfile, IDATE, MID, count(rcount)  rcount FROM
				(SELECT hi.IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, ifile, isfile, IDATE, MID, ri.iid rcount
				FROM hotel_inquiry hi, reply_inquiry ri 
				where hi.iid = ri.iid(+)
				)
				GROUP BY IID, HOTELNAME, CATEGORY, TITLE, SECRET, SECRETNUM, CONTENT, ifile, isfile, IDATE, MID
				order by idate desc
			)
		) 
	WHERE iid = #{iid}
